import React from 'react';
import { Avatar, Button ,IconButton, Toolbar, Typography, makeStyles }  from '@material-ui/core';
import userPhoto from '../../../logo.svg';
import { useStateValue } from '../../../context/store';

const useStyles = makeStyles((theme) => ({
    DesktopSctn: {
        display: "none",
        [theme.breakpoints.up("md")]: {
            display: "flex"
        }
    },
    MobileSctn: {
        display: "flex",
        [theme.breakpoints.up("md")]: {
            display: "none"
        }
    },
    grow: {
        flexGrow: 1
    },
    avatarSize: {
        height: 40,
        width: 40
    }
}));

const BarSesion = () => {
    const classes = useStyles();
    const [{userSessionR}, dispatch] = useStateValue();


    return (
        <Toolbar>
            <IconButton color = 'inherit'>
                <i className="material-icons">menu</i>
            </IconButton>
            <Typography variant="h6">RamaDeneb's Online Courses</Typography>
            <div className={classes.grow}></div>

            <div className={classes.DesktopSctn}>
                <Button color = 'inherit'>Exit</ Button>
                <Button color = 'inherit'>{userSessionR? userSessionR.user.FullName : ""}</ Button>
                <Avatar src={userPhoto}>
                </Avatar>
            </div>

            <div className={classes.MobileSctn}>
                <IconButton color='inherit'>
                    <i className="material-icons">more_vert</i>
                </IconButton>
            </div>   



        </Toolbar>
    )

}

export default BarSesion;
