import React, { useState } from "react";
import {
  Avatar,
  Button,
  Drawer,
  IconButton,
  Toolbar,
  Typography,
} from "@mui/material";

import { useNavigate } from 'react-router-dom';
import { LeftMenu } from "./LeftMenu";
import { RightMenu } from "./RightMenu";

import userPhoto from "../../../logo.svg";
import { useStateValue } from "../../../context/store";

const BarSesion = () => {
  const [{ userSessionR }, dispatch] = useStateValue();
  const [leftOpen, setLeftOpen] = useState(false);
  const [rightOpen, setRightOpen] = useState(false);
  const navigate = useNavigate();

  const closeLeftMenu = () => setLeftOpen(false);
  const closeRightMenu = () => setRightOpen(false);
  const openLeftMenu = () => setLeftOpen(true);
  const openRightMenu = () => setRightOpen(true);

  const closeSession = () => {
    localStorage.removeItem("user_token");
    navigate('/auth');
  };

  return (
    <>
      <Drawer 
        open={leftOpen}
        onClose={closeLeftMenu}
        anchor="left"
      >
        <div 
          style={{ width: 250 }} 
          onKeyDown={closeLeftMenu} 
          onClick={closeLeftMenu}
        >
          <LeftMenu />
        </div>
      </Drawer>

      <Drawer 
        open={rightOpen}
        onClose={closeRightMenu}
        anchor="right"
      >
        <div 
          role="button" 
          onKeyDown={closeRightMenu} 
          onClick={closeRightMenu}
        >
          <RightMenu closeSession={closeSession} />
        </div>
      </Drawer>

      <Toolbar>
        <IconButton color="inherit" onClick={openLeftMenu}>
          <i className="material-icons">menu</i>
        </IconButton>

        <Typography variant="h6">RamaDeneb's Online Courses</Typography>
        <div style={{ flexGrow: 1 }}></div>

        <div sx={{ 
          display: { xs: 'none', md: 'flex' },
          alignItems: 'center',
          gap: 2 
        }}>
          <Button color="inherit" onClick={closeSession}>Exit</Button>
          <Button color="inherit">
            {userSessionR ? userSessionR.user.FullName : ""}
          </Button>
          <Avatar src={userPhoto} />
        </div>

        <div sx={{ 
          display: { xs: 'flex', md: 'none' } 
        }}>
          <IconButton color="inherit" onClick={openRightMenu}>
            <i className="material-icons">more_vert</i>
          </IconButton>
        </div>
      </Toolbar>
    </>
  );
};

export default BarSesion;
