import React, {useState } from "react";
import {
  Avatar,
  Button,
  Drawer,
  IconButton,
  Toolbar,
  Typography,
  makeStyles,
  List,
  ListItemText,
  ListItem
} from "@material-ui/core";
import userPhoto from "../../../logo.svg";
import { useStateValue } from "../../../context/store";

const useStyles = makeStyles((theme) => ({
  DesktopSctn: {
    display: "none",
    [theme.breakpoints.up("md")]: {
      display: "flex",
    },
  },
  MobileSctn: {
    display: "flex",
    [theme.breakpoints.up("md")]: {
      display: "none",
    },
  },
  grow: {
    flexGrow: 1,
  },
  avatarSize: {
    height: 40,
    width: 40,
  },
  list: {
    width: 250,
  },
  ListItem: {
    fontSize: "14",
    fontWeight: 600,
    paddingLeft: "15",
    color: "#212121",
  }
}));

const BarSesion = () => {
  const classes = useStyles();
  const [{ userSessionR }, dispatch] = useStateValue();
  const [leftOpen, setLefOpen] = useState(false);

  const closeLeftMenu = () =>{
    setLefOpen(false);
  };

  return (
    <>
      <Drawer 
        open={leftOpen}
        onClose={closeLeftMenu}
        anchor="left"
      > 
        <div className={classes.lits} onKeyDown={closeLeftMenu}>
            <List>
                <ListItem button>
                    <i className="material-icons>">account box</i>
                    <ListItemText classes={{primary: classes.ListItemText}} primary="Perfil" /> 
                </ListItem>
            </List>
        </div>

      </Drawer>
      <Toolbar>
        <IconButton color="inherit">
          <i className="material-icons">menu</i>
        </IconButton>
        <Typography variant="h6">RamaDeneb's Online Courses</Typography>
        <div className={classes.grow}></div>

        <div className={classes.DesktopSctn}>
          <Button color="inherit">Exit</Button>
          <Button color="inherit">
            {userSessionR ? userSessionR.user.FullName : ""}
          </Button>
          <Avatar src={userPhoto}></Avatar>
        </div>

        <div className={classes.MobileSctn}>
          <IconButton color="inherit">
            <i className="material-icons">more_vert</i>
          </IconButton>
        </div>
      </Toolbar>
    </>
  );
};

export default BarSesion;
